dataSample.sql:
CREATE TABLE IF NOT EXISTS `PREFIX_leofeature_compare` ( `id_compare` int(10) unsigned NOT NULL AUTO_INCREMENT, `id_customer` int(10) unsigned NOT NULL, PRIMARY KEY (`id_compare`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leofeature_compare_product` ( `id_compare` int(10) unsigned NOT NULL, `id_product` int(10) unsigned NOT NULL, `date_add` datetime NOT NULL, `date_upd` datetime NOT NULL, PRIMARY KEY (`id_compare`,`id_product`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leofeature_product_review` ( `id_product_review` int(10) unsigned NOT NULL AUTO_INCREMENT, `id_product` int(10) unsigned NOT NULL, `id_customer` int(10) unsigned NOT NULL, `id_guest` int(10) unsigned DEFAULT NULL, `title` varchar(64) DEFAULT NULL, `content` text NOT NULL, `customer_name` varchar(64) DEFAULT NULL, `grade` float unsigned NOT NULL, `validate` tinyint(1) NOT NULL, `deleted` tinyint(1) NOT NULL, `date_add` datetime NOT NULL, PRIMARY KEY (`id_product_review`), KEY `id_product` (`id_product`), KEY `id_customer` (`id_customer`), KEY `id_guest` (`id_guest`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leofeature_product_review_criterion` ( `id_product_review_criterion` int(10) unsigned NOT NULL AUTO_INCREMENT, `id_product_review_criterion_type` tinyint(1) NOT NULL, `active` tinyint(1) NOT NULL, PRIMARY KEY (`id_product_review_criterion`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leofeature_product_review_criterion_category` ( `id_product_review_criterion` int(10) unsigned NOT NULL, `id_category` int(10) unsigned NOT NULL, PRIMARY KEY (`id_product_review_criterion`,`id_category`), KEY `id_category` (`id_category`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leofeature_product_review_criterion_lang` ( `id_product_review_criterion` int(11) unsigned NOT NULL, `id_lang` int(11) unsigned NOT NULL, `name` varchar(64) NOT NULL, PRIMARY KEY (`id_product_review_criterion`,`id_lang`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leofeature_product_review_criterion_product` ( `id_product` int(10) unsigned NOT NULL, `id_product_review_criterion` int(10) unsigned NOT NULL, PRIMARY KEY (`id_product`,`id_product_review_criterion`), KEY `id_product_review_criterion` (`id_product_review_criterion`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leofeature_product_review_grade` ( `id_product_review_grade` int(11) NOT NULL AUTO_INCREMENT, `id_product_review` int(10) unsigned NOT NULL, `id_product_review_criterion` int(10) unsigned NOT NULL, `grade` int(10) unsigned NOT NULL, PRIMARY KEY (`id_product_review_grade`), KEY `id_product_review_criterion` (`id_product_review_criterion`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leofeature_product_review_report` ( `id_product_review` int(10) unsigned NOT NULL, `id_customer` int(10) unsigned NOT NULL, PRIMARY KEY (`id_product_review`,`id_customer`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leofeature_product_review_usefulness` ( `id_product_review` int(10) unsigned NOT NULL, `id_customer` int(10) unsigned NOT NULL, `usefulness` tinyint(1) unsigned NOT NULL, PRIMARY KEY (`id_product_review`,`id_customer`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leofeature_wishlist` ( `id_wishlist` int(10) unsigned NOT NULL AUTO_INCREMENT, `id_customer` int(10) unsigned NOT NULL, `token` varchar(64) NOT NULL, `name` varchar(64) NOT NULL, `counter` int(10) unsigned DEFAULT NULL, `id_shop` int(10) unsigned DEFAULT 1, `id_shop_group` int(10) unsigned DEFAULT 1, `date_add` datetime NOT NULL, `date_upd` datetime NOT NULL, `default` int(10) unsigned DEFAULT 0, PRIMARY KEY (`id_wishlist`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leofeature_wishlist_product` ( `id_wishlist_product` int(10) NOT NULL AUTO_INCREMENT, `id_wishlist` int(10) unsigned NOT NULL, `id_product` int(10) unsigned NOT NULL, `id_product_attribute` int(10) unsigned NOT NULL, `quantity` int(10) unsigned NOT NULL, `priority` int(10) unsigned NOT NULL, PRIMARY KEY (`id_wishlist_product`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
